      --dbms_output.put_line('REGRA_MENSLID_REAJ1(vnr_regra,reaj_atual);' ||
      --                     vnr_regra || ';' || tab_preco_refer.reaj_atual);
    
      --insere a data de reajuste da regra
      p_insert_regra_menslid_reaj(pAoSimular,
