  --Procedure para insert da regra_menslid - Utilizada na p_migra_regra_proposta
  procedure p_insert_regra_menslid(pAoSimular   varchar2,
                                   pcdd_regra   number,
                                   pdes_regra   varchar2,
                                   pdat_inic    Date,
                                   pdat_fim     date,
                                   pcdn_tip_idx Number) is
  begin
    insert into regra_menslid_sim
      (cdd_regra,
       des_regra,
       dat_inic,
       dat_fim,
       des_obs,
       cdn_tip_idx,
       in_tipo_regra,
       log_livre_1, --indicador se  regra modelo
       progress_recid)
    values
      (pcdd_regra,
       pdes_regra,
       trunc(pdat_inic, 'mm'), --primeiro dia do ms
       last_day(pdat_fim), --ultimo dia do ms
       ' ',
       pcdn_tip_idx, /*1 - Default*/
       1,
       0, -- 1:  regra modelo; 0:  regra de um contrato/beneficirio
       regra_menslid_sim_seq.nextval);

    if pAoSimular = 'N' then
      insert into gp.regra_menslid
        (cdd_regra,
         des_regra,
         dat_inic,
         dat_fim,
         des_obs,
         cdn_tip_idx,
         in_tipo_regra,
         log_livre_1, --indicador se  regra modelo
         progress_recid)
      values
        (pcdd_regra,
         pdes_regra,
         trunc(pdat_inic, 'mm'), --primeiro dia do ms
         last_day(pdat_fim), --ultimo dia do ms
         ' ',
         1,
         1,
         0, -- 1:  regra modelo; 0:  regra de um contrato/beneficirio
         gp.regra_menslid_seq.nextval);
    end if;
  end p_insert_regra_menslid;

