    cursor c_servico_autorizacao(pnrserv_nrautorizacao number) is
    --servios ja executados
      --select nvl(ds.cdvalor_externo, nvl(sr.cdservico_prest, sr.cdservico)) cdservico_refer, --alterado pois no usa cdservico_prest ao criar AMBPROCE, e sim cdservico
