             log_2,
             log_3,
             log_4,
             log_5,
             nr_endereco,
             id_pessoa_old,
             int_3,
             int_4,
             int_5,
             u_char_1, --> TPENDERECO: COBR, COM, CON1, CON2, CON3, CON4, COR, ENT, RES
             u_char_2,
             u_char_3,
             u_date_1,
             u_date_2,
             u_date_3,
             u_dec_1,
             u_dec_2,
             u_dec_3,
             u_int_1,
             u_int_2,
             u_int_3,
             u_log_1, --> 1-AOCORRESPONDENCIA='S'; 0-AOCORRESPONDENCIA='N';
             u_log_2,
