                 and pr.cd_usuario = '0'
                 and pr.u##cd_cid = ' '
                 and pr.cd_esp_amb = pcdespecialidadeproc
                 and (pr.cd_grupo_proc_amb = pcdgrupoproc or
                     pr.cd_grupo_proc_amb = '0')
                 and (pr.cd_procedimento = pcdprocedimento or
