prompt
create or replace trigger UNICOOGPS."TR_BD_IMPORT_GUIA"
  before delete on "GP"."IMPORT_GUIA"
  for each row
declare

begin
    if :old.ind_tip_guia in ('S', 'E') then
      for a in (select igs.val_seqcial
                  from import_guia_sadt igs
                 where igs.val_seq_import = :old.num_seqcial) loop
        for b in (select ig.val_seqcial, ig.progress_recid
                    from import_guia_movto ig
                   where ig.val_seq_guia = a.val_seqcial
                     and ig.u##ind_tip_guia = 'SADT') loop
          delete from import_movto_glosa img
           where img.u##in_modulo = 'AT'
             and img.u##ind_tip_guia = 'SADT'
             and img.val_seqcial_movto = b.val_seqcial
             and img.val_seq_guia = a.val_seqcial;
          delete from import_guia_movto ig
           where ig.progress_recid = b.progress_recid;
        end loop;
      end loop;
    elsif :old.ind_tip_guia = 'C' then
      for a in (select igc.num_seqcial_guia
                  from import_guia_con igc
                 where igc.num_seq_import = :old.num_seqcial) loop
        for b in (select ig.val_seqcial, ig.progress_recid
                    from import_guia_movto ig
                   where ig.val_seq_guia = a.num_seqcial_guia
                     and ig.u##ind_tip_guia = 'CONS') loop
          delete from import_movto_glosa img
           where img.u##in_modulo = 'AT'
             and img.u##ind_tip_guia = 'CONS'
             and img.val_seqcial_movto = b.val_seqcial
             and img.val_seq_guia = a.num_seqcial_guia;
          delete from import_guia_movto ig
           where ig.progress_recid = b.progress_recid;
        end loop;
      end loop;
    elsif :old.ind_tip_guia = 'I' then
      for a in (select igi.num_seqcial_guia
                  from import_guia_intrcao igi
                 where igi.num_seq_import = :old.num_seqcial) loop
        for b in (select ig.val_seqcial, ig.progress_recid
                    from import_guia_movto ig
                   where ig.val_seq_guia = a.num_seqcial_guia
                     and ig.u##ind_tip_guia = 'INTE') loop
          delete from import_movto_glosa img
           where img.u##in_modulo = 'AT'
             and img.u##ind_tip_guia = 'INTE'
             and img.val_seqcial_movto = b.val_seqcial
             and img.val_seq_guia = a.num_seqcial_guia;
          delete from import_guia_movto ig
           where ig.progress_recid = b.progress_recid;
        end loop;
      end loop;
    elsif :old.ind_tip_guia = 'Q' then
      delete from import_guia_movto ig
       where (ig.u##ind_tip_guia = 'QUIM' and exists
              (select 1
                 from import_anexo_quimio iao
                where ig.val_seq_guia = iao.num_seqcial
                  and exists
                (select 1
                         from import_anexo_solicit ias
                        where num_seq_import = :old.num_seqcial
                          and iao.num_seqcial_import_anexo = ias.num_seqcial)));
    elsif :old.ind_tip_guia = 'R' then
      delete from import_guia_movto ig
       where (ig.u##ind_tip_guia = 'RADI' and exists
              (select 1
                 from import_anexo_radio iao
                where ig.val_seq_guia = iao.num_seqcial
                  and exists
                (select 1
                         from import_anexo_solicit ias
                        where num_seq_import = :old.num_seqcial
                          and iao.num_seqcial_import_anexo = ias.num_seqcial)));
    elsif :old.ind_tip_guia = 'O' then
      delete from import_guia_movto ig
       where (ig.u##ind_tip_guia = 'OPME' and exists
              (select 1
                 from import_anexo_opme iao
                where ig.val_seq_guia = iao.num_seqcial
                  and exists
                (select 1
                         from import_anexo_solicit ias
                        where num_seq_import = :old.num_seqcial
                          and iao.num_seqcial_import_anexo = ias.num_seqcial)));
    end if;
    delete from import_guia_sadt where val_seq_import = :old.num_seqcial;
    delete from import_guia_intrcao
     where num_seq_import = :old.num_seqcial;
    delete from import_anexo_opme iao
     where exists
     (select 1
              from import_anexo_solicit ias
             where num_seq_import = :old.num_seqcial
               and iao.num_seqcial_import_anexo = ias.num_seqcial);
    delete from import_anexo_quimio iaq
     where exists
     (select 1
              from import_anexo_solicit ias
             where num_seq_import = :old.num_seqcial
               and iaq.num_seqcial_import_anexo = ias.num_seqcial);
    delete from import_anexo_radio iar
     where exists
     (select 1
              from import_anexo_solicit ias
             where num_seq_import = :old.num_seqcial
               and iar.num_seqcial_import_anexo = ias.num_seqcial);
    delete from import_guia_con where num_seq_import = :old.num_seqcial;
    delete from import_anexo_solicit
     where num_seq_import = :old.num_seqcial;
    delete from erro_process_import
     where num_seqcial_control = :old.num_seqcial_control;
    delete from import_guia_histor where val_seq_guia = :old.num_seqcial;
end TR_BD_import_guia;
